import React, { useEffect, useState } from 'react';
import Logo from "../logo.svg";
import "bootstrap/dist/css/bootstrap.css";
import { NavLink, useNavigate } from "react-router-dom";
import { useAuth } from "./AuthProvider";

export default function Navbar() {
    const { logout, user } = useAuth(); // assuming useAuth provides user information
    const [role, setRole] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const checkUserRole = async () => {
            try {
                const userResponse = await fetch('/user/checkUser'); // Adjust the API endpoint
                if (userResponse.ok) {
                    setRole('user');
                    return;
                }
                const tellerResponse = await fetch('/teller/checkTeller'); // Adjust the API endpoint
                if (tellerResponse.ok) {
                    setRole('teller');
                }
            } catch (error) {
                console.error("Error checking user role:", error);
            }
        };

        if (user) {
            checkUserRole();
        }
    })
    const handleLogout = () => {
        logout();
        navigate("/login");
    };

    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <NavLink className="navbar-brand" to="/">
                <img src={Logo} alt="Logo" style={{ width: '25%' }} />
            </NavLink>
            <div className="collapse navbar-collapse" id="navbarSupportedContent">
                <ul className="navbar-nav ml-auto">
                    <li className="nav-item">
                        <NavLink className="nav-link user" to="/login">
                            Login
                        </NavLink>
                    </li>
                    <li className="nav-item">
                        <NavLink className="nav-link user" to="/viewtransactions">
                            View my transactions
                        </NavLink>
                    </li>
                    <li className="nav-item">
                        <NavLink className="nav-link user" to="/newtransaction">
                            New Transaction
                        </NavLink>
                    </li>
                    <li className="nav-item">
                        <button className="nav-link btn btn-link" onClick={handleLogout}>
                            Logout
                        </button>
                    </li>
                    <li className="nav-item">
                        <NavLink className="nav-link" to="/signup">
                            Sign Up
                        </NavLink>
                    </li>
                    <li className="nav-item">
                        <NavLink className="nav-link" to="/tellerLogin">
                            Employees
                        </NavLink>
                    </li>
                    <li className='nav-item teller'>
                        <NavLink className="nav-link teller" to="/addTeller">
                            Add Teller
                        </NavLink>
                    </li>
                    <li className='nav-item'>
                        <NavLink className="nav-link teller" to="/addViewAllTransactions">
                            View All Transactions
                        </NavLink>
                    </li>
                </ul>
            </div>
        </nav>
    );
}
