import React, { useEffect, useState } from 'react';
import Logo from "../logo.svg";
import "bootstrap/dist/css/bootstrap.css";
import { NavLink, useNavigate } from "react-router-dom";
import { useAuth } from "./AuthProvider";

export default function Navbar() {
  const { logout, user } = useAuth();
  const [role, setRole] = useState(null);
  const [isLoading, setIsLoading] = useState(true); // Add loading state
  const [error, setError] = useState(null);    // Add error state
  const navigate = useNavigate();

  useEffect(() => {
    const checkUserRole = async () => {
      if (user) { 
        try {
          setIsLoading(true);
          const accountnum = localStorage.getItem("accountnum");
          const response = await fetch(`https://localhost:3001/user/checkUser?accountnum=${accountnum}`, {
            method: "GET",
            headers: {
              "Content-Type": "application/json",
            },
          });

          if (response.ok) {
            const data = await response.json();
            setRole(data.role); 
          } else {
            const errorData = await response.json();
            setError(errorData.message || "Error fetching role");
          }
        } catch (error) {
          console.error("Error checking user role:", error);
          setError("Network error"); 
        } finally {
          setIsLoading(false);
        }
      } else {
        // Reset role and loading/error state when user logs out
        setRole(null);
        setIsLoading(false);
        setError(null);
      }
    };

    checkUserRole();
  }, [user]); // Only re-run when user changes

  const handleLogout = () => {
    logout();
    navigate("/login");
  };
    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <NavLink className="navbar-brand" to="/">
                <img src={Logo} alt="Logo" style={{ width: '25%' }} />
            </NavLink>
            <div className="collapse navbar-collapse" id="navbarSupportedContent">
                <ul className="navbar-nav ms-auto"> {}
                    {!user && (
                        <>
                            <li className="nav-item">
                                <NavLink className="nav-link" to="/login">
                                    Login
                                </NavLink>
                            </li>
                            <li className="nav-item">
                                <NavLink className="nav-link" to="/signup">
                                    Sign Up
                                </NavLink>
                            </li>
                            <li className="nav-item">
                                <NavLink className="nav-link" to="/tellerLogin">
                                    Employees
                                </NavLink>
                            </li>
                        </>
                    )}
                    {user && role === 'user' && (
                        <>
                            <li className="nav-item">
                                <NavLink className="nav-link" to="/viewtransactions">
                                    View my transactions
                                </NavLink>
                            </li>
                            <li className="nav-item">
                                <NavLink className="nav-link" to="/newtransaction">
                                    New Transaction
                                </NavLink>
                            </li>
                        </>
                    )}
                    {user && role === 'teller' && (
                        <>
                            <li className="nav-item">
                                <NavLink className="nav-link" to="/addTeller">
                                    Add Teller
                                </NavLink>
                            </li>
                            <li className="nav-item">
                                <NavLink className="nav-link" to="/addViewAllTransactions">
                                    View All Transactions
                                </NavLink>
                            </li>
                        </>
                    )}
                    {user && (
                        <li className="nav-item">
                            <button className="nav-link btn btn-link" onClick={handleLogout}>
                                Logout
                            </button>
                        </li>
                    )}
                </ul>
            </div>
        </nav>
    );
}
